package com.v5kf.client.ui.utils;

public class V5Size {
	private int width;
	private int height;
	
	public V5Size() {
		// TODO Auto-generated constructor stub
	}
	
	public V5Size(int w, int h) {
		this.width = w;
		this.height = h;
	}
	
	public int getWidth() {
		return width;
	}
	
	public void setWidth(int width) {
		this.width = width;
	}
	
	public int getHeight() {
		return height;
	}
	
	public void setHeight(int height) {
		this.height = height;
	}
}